import React, { useEffect, useState } from "react";
import "../assets/Styles/Dashboard.css";
import { Line } from "react-chartjs-2"; // Chart.js library

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [roles, setRoles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    Promise.all([
      fetch("http://localhost:5000/users").then((response) => response.json()),
      fetch("http://localhost:5000/roles").then((response) => response.json()),
    ])
      .then(([usersData, rolesData]) => {
        setUsers(usersData);
        setRoles(rolesData);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setLoading(false);
      });
  }, []);

  const totalUsers = users.length;
  const activeUsers = users.filter((user) => user.status === "Active").length;
  const pendingRoleAssignments = users.filter((user) => !user.role).length;
  const totalRoles = roles.length;

  const data = {
    labels: ["Users", "Roles", "Active Users"],
    datasets: [
      {
        label: "Metrics",
        data: [totalUsers, totalRoles, activeUsers],
        fill: false,
        backgroundColor: "rgba(75,192,192,0.2)",
        borderColor: "rgba(75,192,192,1)",
        tension: 0.1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
    },
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <div className="cards">
        <div className="card">
          <span role="img" aria-label="users">
            üë•
          </span>
          Total Users: {totalUsers}
        </div>
        <div className="card">
          <span role="img" aria-label="roles">
            üîê
          </span>
          Total Roles: {totalRoles}
        </div>
        <div className="card">
          <span role="img" aria-label="active">
            ‚úÖ
          </span>
          Active Users: {activeUsers}
        </div>
        <div className="card">
          <span role="img" aria-label="pending">
            ‚ö†Ô∏è
          </span>
          Pending Role Assignments: {pendingRoleAssignments}
        </div>
      </div>

      {/* Chart */}
      <div className="chart-container">
        <h2>Metrics Overview</h2>
        <Line data={data} options={options} />
      </div>
    </div>
  );
};

export default Dashboard;
