import React, { useState } from "react";
import "../assets/Styles/AddUserPage.css";

const AddUserPage = () => {
  const [newUser, setNewUser] = useState({
    fullName: "",
    username: "",
    email: "",
    role: "User",
    permissions: [],
    phoneNumber: "",
    department: "",
    status: "Active",
    expirationDate: "",
    notes: "",
  });
  const [photo, setPhoto] = useState(null); // State for photo upload

  const handleAdd = (e) => {
    e.preventDefault();
    if (newUser.password !== newUser.confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    const formData = new FormData();
    formData.append("photo", photo); // Append photo file
    Object.keys(newUser).forEach((key) => {
      formData.append(key, newUser[key]); // Append other fields
    });

    fetch("http://localhost:5000/users", {
      method: "POST",
      body: formData, // Use FormData
    })
      .then((response) => response.json())
      .then(() => {
        setNewUser({
          fullName: "",
          username: "",
          email: "",
          role: "User",
          permissions: [],
          phoneNumber: "",
          department: "",
          status: "Active",
          expirationDate: "",
          notes: "",
        });
        setPhoto(null); // Reset photo
        alert("User added successfully!");
      })
      .catch((error) => console.error("Error adding user:", error));
  };

  return (
    <div className="add-user-page">
      <h2>Add New User</h2>
      <form onSubmit={handleAdd} className="user-form">
        {/* Basic User Information */}
        <label>
        Full Name
        <input
          type="text"
          placeholder="Full Name"
          value={newUser.fullName}
          onChange={(e) =>
            setNewUser({ ...newUser, fullName: e.target.value })
          }
          required
        />
        </label>
        <label>
            Username
        <input
          type="text"
          placeholder="Username"
          value={newUser.username}
          onChange={(e) =>
            setNewUser({ ...newUser, username: e.target.value })
          }
          required
        />
        </label>
        <input
          type="email"
          placeholder="Email Address"
          value={newUser.email}
          onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
          required
        />

        {/* Role Assignment */}
        <select
          value={newUser.role}
          onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
        >
          <option value="Admin">Admin</option>
          <option value="Editor">Editor</option>
          <option value="Viewer">Viewer</option>
          <option value="User">User</option>
        </select>

        {/* Permissions */}
        <div>
          <label>
            <input
              type="checkbox"
              checked={newUser.permissions.includes("View Reports")}
              onChange={(e) => {
                const newPermissions = e.target.checked
                  ? [...newUser.permissions, "View Reports"]
                  : newUser.permissions.filter((perm) => perm !== "View Reports");
                setNewUser({ ...newUser, permissions: newPermissions });
              }}
            />
            View Reports
          </label>
          <label>
            <input
              type="checkbox"
              checked={newUser.permissions.includes("Edit Content")}
              onChange={(e) => {
                const newPermissions = e.target.checked
                  ? [...newUser.permissions, "Edit Content"]
                  : newUser.permissions.filter((perm) => perm !== "Edit Content");
                setNewUser({ ...newUser, permissions: newPermissions });
              }}
            />
            Edit Content
          </label>
        </div>

        {/* Profile Information */}
        <input
          type="tel"
          placeholder="Phone Number"
          value={newUser.phoneNumber}
          onChange={(e) =>
            setNewUser({ ...newUser, phoneNumber: e.target.value })
          }
        />
        <input
          type="text"
          placeholder="Department"
          value={newUser.department}
          onChange={(e) =>
            setNewUser({ ...newUser, department: e.target.value })
          }
        />

        {/* Status and Activation */}
        <select
          value={newUser.status}
          onChange={(e) => setNewUser({ ...newUser, status: e.target.value })}
        >
          <option value="Active">Active</option>
          <option value="Inactive">Inactive</option>
        </select>
        <input
          type="date"
          placeholder="Expiration Date (Optional)"
          value={newUser.expirationDate}
          onChange={(e) =>
            setNewUser({ ...newUser, expirationDate: e.target.value })
          }
        />

        {/* Notes */}
        <textarea
          placeholder="Additional Notes"
          value={newUser.notes}
          onChange={(e) => setNewUser({ ...newUser, notes: e.target.value })}
        ></textarea>

        {/* Profile Image */}
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setPhoto(e.target.files[0])}
          required
        />

        {/* Buttons */}
        <button type="submit" className="form-submit-button">
          Add User
        </button>
        <button
          type="reset"
          onClick={() => {
            setNewUser({
              fullName: "",
              username: "",
              email: "",
              role: "User",
              permissions: [],
              phoneNumber: "",
              department: "",
              status: "Active",
              expirationDate: "",
              notes: "",
            });
            setPhoto(null);
          }}
        >
          Reset
        </button>
      </form>
    </div>
  );
};

export default AddUserPage;
