import React, { useState, useEffect } from "react";
import "../assets/Styles/UserTable.css";

const UserTable = () => {
  const [users, setUsers] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentUser, setCurrentUser] = useState({ id: null, name: "", role: "", status: "" });
  const [newUser, setNewUser] = useState({ name: "", role: "User", status: "Active" });

  // Fetch data from json-server when the component mounts
  useEffect(() => {
    fetch("http://localhost:5000/users")
      .then((response) => response.json())
      .then((data) => setUsers(data))
      .catch((error) => console.error("Error fetching users:", error));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/users/${id}`, {
      method: "DELETE",
    })
      .then(() => setUsers(users.filter((user) => user.id !== id)))
      .catch((error) => console.error("Error deleting user:", error));
  };

  const handleEdit = (user) => {
    setIsEditing(true);
    setCurrentUser(user);
  };

  const handleUpdate = () => {
    fetch(`http://localhost:5000/users/${currentUser.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(currentUser),
    })
      .then(() => {
        setUsers(
          users.map((user) =>
            user.id === currentUser.id ? currentUser : user
          )
        );
        setIsEditing(false);
        setCurrentUser({ id: null, name: "", role: "", status: "" });
      })
      .catch((error) => console.error("Error updating user:", error));
  };

  const handleAdd = () => {
    fetch("http://localhost:5000/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newUser),
    })
      .then((response) => response.json())
      .then((user) => {
        setUsers([...users, user]);
        setNewUser({ name: "", role: "User", status: "Active" });
      })
      .catch((error) => console.error("Error adding user:", error));
  };

  return (
    <div>
      <h2>User Management</h2>
      <div>
        <h3>{isEditing ? "Edit User" : "Add New User"}</h3>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            isEditing ? handleUpdate() : handleAdd();
          }}
        >
          <input
            type="text"
            placeholder="Name"
            value={isEditing ? currentUser.name : newUser.name}
            onChange={(e) =>
              isEditing
                ? setCurrentUser({ ...currentUser, name: e.target.value })
                : setNewUser({ ...newUser, name: e.target.value })
            }
            required
          />
          <select
            value={isEditing ? currentUser.role : newUser.role}
            onChange={(e) =>
              isEditing
                ? setCurrentUser({ ...currentUser, role: e.target.value })
                : setNewUser({ ...newUser, role: e.target.value })
            }
          >
            <option value="Admin">Admin</option>
            <option value="User">User</option>
          </select>
          <select
            value={isEditing ? currentUser.status : newUser.status}
            onChange={(e) =>
              isEditing
                ? setCurrentUser({ ...currentUser, status: e.target.value })
                : setNewUser({ ...newUser, status: e.target.value })
            }
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
          <button type="submit">{isEditing ? "Update" : "Add"}</button>
          {isEditing && (
            <button
              type="button"
              onClick={() => {
                setIsEditing(false);
                setCurrentUser({ id: null, name: "", role: "", status: "" });
              }}
            >
              Cancel
            </button>
          )}
        </form>
      </div>
      <input type="text" placeholder="Search users..." />
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>{user.status}</td>
              <td>
                <button onClick={() => handleEdit(user)}>Edit</button>
                <button onClick={() => handleDelete(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserTable;
