import React, { useState, useEffect } from "react";
import "../assets/Styles/UserTable.css";

const UserTable = () => {
  const [users, setUsers] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false); // Toggle form visibility
  const [newUser, setNewUser] = useState({ name: "", role: "User", status: "Active" });

  // Fetch data from json-server when the component mounts
  useEffect(() => {
    fetch("http://localhost:5000/users")
      .then((response) => response.json())
      .then((data) => setUsers(data))
      .catch((error) => console.error("Error fetching users:", error));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/users/${id}`, {
      method: "DELETE",
    })
      .then(() => setUsers(users.filter((user) => user.id !== id)))
      .catch((error) => console.error("Error deleting user:", error));
  };
  

  const handleAdd = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newUser),
    })
      .then((response) => response.json())
      .then((user) => {
        setUsers([...users, user]);
        setNewUser({ name: "", role: "User", status: "Active" });
        setShowAddForm(false); // Hide form after adding
      })
      .catch((error) => console.error("Error adding user:", error));
  };

  return (
    <div>
      <h2>User Management</h2>
      <div style={{ textAlign: "right", marginBottom: "10px" }}>
        <button
          onClick={() => setShowAddForm(!showAddForm)}
          style={{
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            padding: "10px 20px",
            cursor: "pointer",
            borderRadius: "4px",
          }}
        >
          Add User <span style={{ fontWeight: "bold", fontSize: "18px" }}>+</span>
        </button>
      </div>
      {showAddForm && (
        <form
          onSubmit={handleAdd}
          style={{
            border: "1px solid #ccc",
            padding: "15px",
            borderRadius: "5px",
            marginBottom: "15px",
          }}
        >
          <h3>Add New User</h3>
          <input
            type="text"
            placeholder="Name"
            value={newUser.name}
            onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
            required
            style={{
              marginBottom: "10px",
              padding: "8px",
              width: "100%",
              boxSizing: "border-box",
            }}
          />
          <select
            value={newUser.role}
            onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
            style={{
              marginBottom: "10px",
              padding: "8px",
              width: "100%",
              boxSizing: "border-box",
            }}
          >
            <option value="Admin">Admin</option>
            <option value="User">User</option>
          </select>
          <select
            value={newUser.status}
            onChange={(e) => setNewUser({ ...newUser, status: e.target.value })}
            style={{
              marginBottom: "10px",
              padding: "8px",
              width: "100%",
              boxSizing: "border-box",
            }}
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
          <button
            type="submit"
            style={{
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              padding: "10px 20px",
              cursor: "pointer",
              borderRadius: "4px",
            }}
          >
            Add
          </button>
        </form>
      )}
      <input type="text" placeholder="Search users..." />
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>{user.status}</td>
              <td>
                <button
                  onClick={() => handleDelete(user.id)}
                  style={{
                    backgroundColor: "#f44336",
                    color: "white",
                    border: "none",
                    padding: "5px 10px",
                    cursor: "pointer",
                    borderRadius: "4px",
                  }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserTable;
