import React, { useEffect, useState } from "react";
import "../assets/Styles/Dashboard.css";

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [roles, setRoles] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch users and roles from the json-server
  useEffect(() => {
    Promise.all([
      fetch("http://localhost:5000/users").then((response) => response.json()),
      fetch("http://localhost:5000/roles").then((response) => response.json())
    ])
      .then(([usersData, rolesData]) => {
        setUsers(usersData);
        setRoles(rolesData);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setLoading(false);
      });
  }, []);

  // Calculate total users, active users, pending role assignments
  const totalUsers = users.length;
  const activeUsers = users.filter((user) => user.status === "Active").length;
  const pendingRoleAssignments = users.filter((user) => !user.role).length;
  const totalRoles = roles.length;

  // Extract the latest permissions from users
  const latestPermissions = users
    .flatMap((user) =>
      user.permissions?.map((perm) => ({
        userName: user.name,
        permission: perm.permission,
        date: perm.date
      }))
    )
    .sort((a, b) => new Date(b.date) - new Date(a.date)) // Sort by date descending
    .slice(0, 5); // Get the latest 5 permissions

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="dashboard">
      <div className="dashboard-container">
        {/* First 4 cards in a column */}
        <div className="stats-column">
          <div className="card">
            <i className="icon">👥</i>
            <div className="card-info">
              <h3>Total Users</h3>
              <p>{totalUsers}</p>
            </div>
          </div>
          <div className="card">
            <i className="icon">🔐</i>
            <div className="card-info">
              <h3>Total Roles</h3>
              <p>{totalRoles}</p>
            </div>
          </div>
          <div className="card">
            <i className="icon">✅</i>
            <div className="card-info">
              <h3>Active Users</h3>
              <p>{activeUsers}</p>
            </div>
          </div>
          <div className="card">
            <i className="icon">⚠️</i>
            <div className="card-info">
              <h3>Pending Role Assignments</h3>
              <p>{pendingRoleAssignments}</p>
            </div>
          </div>
        </div>

        {/* Latest Permissions section in a separate column */}
        <div className="permissions-section">
          <h2>Latest Permissions</h2>
          <div className="permissions-list">
            {latestPermissions.length > 0 ? (
              latestPermissions.map((permission, index) => (
                <div key={index} className="permission-item">
                  <p>
                    <strong>{permission.userName}</strong> was granted{" "}
                    <strong>{permission.permission}</strong> permission on{" "}
                    <em>{new Date(permission.date).toLocaleDateString()}</em>
                  </p>
                </div>
              ))
            ) : (
              <p>No recent permissions to display.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
