import React, { useEffect, useState } from "react";
import "../assets/Styles/Dashboard.css";

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [roles, setRoles] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch users and roles from the json-server
  useEffect(() => {
    Promise.all([
      fetch("http://localhost:5000/users").then((response) => response.json()),
      fetch("http://localhost:5000/roles").then((response) => response.json()),
    ])
      .then(([usersData, rolesData]) => {
        setUsers(usersData);
        setRoles(rolesData);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setLoading(false);
      });
  }, []);

  // Calculate total users, active users, pending role assignments
  const totalUsers = users.length;
  const activeUsers = users.filter((user) => user.status === "Active").length;
  const pendingRoleAssignments = users.filter((user) => !user.role).length;
  const totalRoles = roles.length;

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <div className="cards">
        <div className="card">üë• Total Users: {totalUsers}</div>
        <div className="card">üîê Total Roles: {totalRoles}</div>
        <div className="card">‚úÖ Active Users: {activeUsers}</div>
        <div className="card">‚ö†Ô∏è Pending Role Assignments: {pendingRoleAssignments}</div>
      </div>
    </div>
  );
};

export default Dashboard;
