import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../assets/Styles/Sidebar.css";
import { FaHome, FaUsers, FaKey, FaShieldAlt, FaChevronLeft, FaCaretDown } from "react-icons/fa";

const Sidebar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [isUsersDropdownOpen, setIsUsersDropdownOpen] = useState(false); // State for dropdown toggle

  return (
    <aside className={`sidebar ${isCollapsed ? "collapsed" : ""}`}>
      <button
        className={`toggle-btn ${isCollapsed ? "rotate" : ""}`}
        onClick={() => setIsCollapsed(!isCollapsed)}
      >
        <FaChevronLeft />
      </button>
      <nav>
        <ul>
          <li>
            <Link to="/">
              <FaHome /> {!isCollapsed && "Dashboard"}
            </Link>
          </li>
          <li>
            <div 
              className="dropdown-toggle" 
              onClick={() => setIsUsersDropdownOpen(!isUsersDropdownOpen)}
            >
              <FaUsers /> {!isCollapsed && "Users"} <FaCaretDown />
            </div>
            {isUsersDropdownOpen && (
              <ul className="dropdown-menu">
                <li>
                  <Link to="/users">Users</Link> {/* Link to the Users page */}
                </li>
                <li>
                  <Link to="/users/add">Add User</Link> {/* Link to the Add User page */}
                </li>
              </ul>
            )}
          </li>
          <li>
            <Link to="/roles">
              <FaKey /> {!isCollapsed && "Roles"}
            </Link>
          </li>
          <li>
            <Link to="/permissions">
              <FaShieldAlt /> {!isCollapsed && "Permissions"}
            </Link>
          </li>
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
