import React, { useState } from "react";
import "../assets/Styles/RoleTable.css";

const RoleTable = () => {
  const [roles, setRoles] = useState([
    { id: 1, name: "Admin", permissions: ["Read", "Write", "Delete"] },
    { id: 2, name: "User", permissions: ["Read"] },
  ]);
  const [newRole, setNewRole] = useState("");
  const [editingRole, setEditingRole] = useState(null);
  const [updatedName, setUpdatedName] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [showAddRoleForm, setShowAddRoleForm] = useState(false);
  const [selectedPermissions, setSelectedPermissions] = useState([]);

  const handleAddRole = () => {
    if (newRole.trim() === "") return;
    const newId = roles.length + 1;
    setRoles([...roles, { id: newId, name: newRole, permissions: selectedPermissions }]);
    setNewRole("");
    setSelectedPermissions([]);
    setShowAddRoleForm(false);  // Hide the form after adding the role
  };

  const handleEditRole = (role) => {
    setEditingRole(role.id);
    setUpdatedName(role.name);
  };

  const handleSaveEdit = (id) => {
    setRoles(
      roles.map((role) =>
        role.id === id ? { ...role, name: updatedName } : role
      )
    );
    setEditingRole(null);
  };

  const handleCancelEdit = () => {
    setEditingRole(null);
    setUpdatedName("");
  };

  const handleDeleteRole = (id) => {
    setRoles(roles.filter((role) => role.id !== id));
  };

  const handleAssignPermission = (id, permission) => {
    setRoles(
      roles.map((role) =>
        role.id === id
          ? {
              ...role,
              permissions: role.permissions.includes(permission)
                ? role.permissions.filter((perm) => perm !== permission)
                : [...role.permissions, permission],
            }
          : role
      )
    );
  };

  const filteredRoles = roles.filter(role =>
    role.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="role-table">
      <h2>Manage Roles</h2>

      {/* Search Bar with Icon */}
      <div className="search-container">
        <div className="search-icon-container">
          <i className="search-icon">üîç</i>
        </div>
        <input
          type="text"
          placeholder="Search Roles"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-bar"
        />
      </div>

      {/* Add Role Button */}
      {!showAddRoleForm ? (
        <button onClick={() => setShowAddRoleForm(true)} className="add-role-button">
          Add Role
        </button>
      ) : (
        <div className="add-role-form">
          <input
            type="text"
            value={newRole}
            placeholder="New Role Name"
            onChange={(e) => setNewRole(e.target.value)}
          />
          <div className="permissions">
            {["Read", "Write", "Delete"].map((permission) => (
              <label key={permission}>
                <input
                  type="checkbox"
                  checked={selectedPermissions.includes(permission)}
                  onChange={() => {
                    if (selectedPermissions.includes(permission)) {
                      setSelectedPermissions(selectedPermissions.filter((perm) => perm !== permission));
                    } else {
                      setSelectedPermissions([...selectedPermissions, permission]);
                    }
                  }}
                />
                {permission}
              </label>
            ))}
          </div>
          <button onClick={handleAddRole}>Save Role</button>
          <button onClick={() => setShowAddRoleForm(false)}>Cancel</button>
        </div>
      )}

      {/* Roles Table */}
      <table>
        <thead>
          <tr>
            <th>Role Name</th>
            <th>Permissions</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredRoles.map((role) => (
            <tr key={role.id}>
              <td>
                {editingRole === role.id ? (
                  <input
                    type="text"
                    value={updatedName}
                    onChange={(e) => setUpdatedName(e.target.value)}
                  />
                ) : (
                  role.name
                )}
              </td>
              <td>
                <div className="permissions">
                  {["Read", "Write", "Delete"].map((permission) => (
                    <label key={permission}>
                      <input
                        type="checkbox"
                        checked={role.permissions.includes(permission)}
                        onChange={() => handleAssignPermission(role.id, permission)}
                      />
                      {permission}
                    </label>
                  ))}
                </div>
              </td>
              <td>
                {editingRole === role.id ? (
                  <>
                    <button onClick={() => handleSaveEdit(role.id)}>Save</button>
                    <button onClick={handleCancelEdit}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => handleEditRole(role)}>Edit</button>
                )}
                <button onClick={() => handleDeleteRole(role.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RoleTable;
