import React, { useState, useEffect } from "react";
import "../assets/Styles/UserTable.css"; // Import the separate CSS file

const UserTable = () => {
  const [users, setUsers] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newUser, setNewUser] = useState({ name: "", role: "User", status: "Active" });
  const [isEditing, setIsEditing] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/users")
      .then((response) => response.json())
      .then((data) => setUsers(data))
      .catch((error) => console.error("Error fetching users:", error));
  }, []);

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/users/${id}`, {
      method: "DELETE",
    })
      .then(() => setUsers(users.filter((user) => user.id !== id)))
      .catch((error) => console.error("Error deleting user:", error));
  };

  const handleAdd = (e) => {
    e.preventDefault();
    fetch("http://localhost:5000/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newUser),
    })
      .then((response) => response.json())
      .then((user) => {
        setUsers([...users, user]);
        setNewUser({ name: "", role: "User", status: "Active" });
        setShowAddForm(false);
      })
      .catch((error) => console.error("Error adding user:", error));
  };

  const handleEdit = (user) => {
    setIsEditing(true);
    setCurrentUser(user);
    setShowAddForm(false);
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5000/users/${currentUser.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(currentUser),
    })
      .then((response) => response.json())
      .then((updatedUser) => {
        setUsers(users.map((user) => (user.id === updatedUser.id ? updatedUser : user)));
        setIsEditing(false);
        setCurrentUser(null);
      })
      .catch((error) => console.error("Error updating user:", error));
  };

  return (
    <div>
      <h2>User Management</h2>

      <div className="add-user-button-container">
        <button
          onClick={() => {
            setShowAddForm(!showAddForm);
            setIsEditing(false);
          }}
          className="add-user-button"
        >
          Add User <span className="plus-sign">+</span>
        </button>
      </div>

      {showAddForm && (
        <form onSubmit={handleAdd} className="user-form">
          <h3>Add New User</h3>
          <input
            type="text"
            placeholder="Name"
            value={newUser.name}
            onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
            required
          />
          <select
            value={newUser.role}
            onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
          >
            <option value="Admin">Admin</option>
            <option value="User">User</option>
          </select>
          <select
            value={newUser.status}
            onChange={(e) => setNewUser({ ...newUser, status: e.target.value })}
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
          <button type="submit" className="form-submit-button">
            Add
          </button>
        </form>
      )}

      {isEditing && (
        <form onSubmit={handleUpdate} className="user-form">
          <h3>Edit User</h3>
          <input
            type="text"
            placeholder="Name"
            value={currentUser.name}
            onChange={(e) => setCurrentUser({ ...currentUser, name: e.target.value })}
            required
          />
          <select
            value={currentUser.role}
            onChange={(e) => setCurrentUser({ ...currentUser, role: e.target.value })}
          >
            <option value="Admin">Admin</option>
            <option value="User">User</option>
          </select>
          <select
            value={currentUser.status}
            onChange={(e) => setCurrentUser({ ...currentUser, status: e.target.value })}
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
          <button type="submit" className="form-submit-button">
            Update
          </button>
        </form>
      )}

      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>{user.status}</td>
              <td>
                <button onClick={() => handleEdit(user)} className="edit-button">
                  Edit
                </button>
                <button onClick={() => handleDelete(user.id)} className="delete-button">
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserTable;
