import React, { useEffect, useState } from "react";
import "../assets/Styles/Dashboard.css";

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [roles, setRoles] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch users and roles from the json-server
  useEffect(() => {
    Promise.all([
      fetch("http://localhost:5000/users").then((response) => response.json()),
      fetch("http://localhost:5000/roles").then((response) => response.json()),
    ])
      .then(([usersData, rolesData]) => {
        setUsers(usersData);
        setRoles(rolesData);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setLoading(false);
      });
  }, []);

  // Calculate total users, active users, pending role assignments
  const totalUsers = users.length;
  const activeUsers = users.filter((user) => user.status === "Active").length;
  const pendingRoleAssignments = users.filter((user) => !user.role).length;
  const totalRoles = roles.length;

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="dashboard">
      <h1 className="dashboard-title">Dashboard</h1>
      <div className="cards">
        <div className="card">
          <i className="icon">üë•</i>
          <div className="card-info">
            <h3>Total Users</h3>
            <p>{totalUsers}</p>
          </div>
        </div>
        <div className="card">
          <i className="icon">üîê</i>
          <div className="card-info">
            <h3>Total Roles</h3>
            <p>{totalRoles}</p>
          </div>
        </div>
        <div className="card">
          <i className="icon">‚úÖ</i>
          <div className="card-info">
            <h3>Active Users</h3>
            <p>{activeUsers}</p>
          </div>
        </div>
        <div className="card">
          <i className="icon">‚ö†Ô∏è</i>
          <div className="card-info">
            <h3>Pending Role Assignments</h3>
            <p>{pendingRoleAssignments}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
