import React, { useState, useEffect } from "react";
import "../assets/Styles/UserTable.css";
import userimg from "../assets/images/user.png"; // Default user image

const UserTable = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState("grid"); // "grid" or "list"
  const [showAddForm, setShowAddForm] = useState(false);
  const [newUser, setNewUser] = useState({
    name: "",
    role: "User",
    status: "Active",
    imageUrl: "",
  });
  const [isEditing, setIsEditing] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  // Fetch users on component mount
  useEffect(() => {
    fetch("http://localhost:5000/users")
      .then((response) => response.json())
      .then((data) => {
        setUsers(data);
        setFilteredUsers(data);
      })
      .catch((error) => console.error("Error fetching users:", error));
  }, []);

  // Handle search
  useEffect(() => {
    setFilteredUsers(
      users.filter((user) =>
        user.name.toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
  }, [searchQuery, users]);

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/users/${id}`, {
      method: "DELETE",
    })
      .then(() => setUsers(users.filter((user) => user.id !== id)))
      .catch((error) => console.error("Error deleting user:", error));
  };

  const handleAdd = (e) => {
    e.preventDefault();
    const userToAdd = {
      ...newUser,
      imageUrl: newUser.imageUrl || userimg,
    };
    fetch("http://localhost:5000/users", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(userToAdd),
    })
      .then((response) => response.json())
      .then((user) => {
        setUsers([...users, user]);
        setNewUser({ name: "", role: "User", status: "Active", imageUrl: "" });
        setShowAddForm(false);
      })
      .catch((error) => console.error("Error adding user:", error));
  };

  const handleEdit = (user) => {
    setIsEditing(true);
    setCurrentUser(user);
    setShowAddForm(false);
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5000/users/${currentUser.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(currentUser),
    })
      .then((response) => response.json())
      .then((updatedUser) => {
        setUsers(users.map((user) => (user.id === updatedUser.id ? updatedUser : user)));
        setIsEditing(false);
        setCurrentUser(null);
      })
      .catch((error) => console.error("Error updating user:", error));
  };

  const handleCancel = () => {
    setShowAddForm(false);
    setIsEditing(false);
    setNewUser({ name: "", role: "User", status: "Active", imageUrl: "" });
    setCurrentUser(null);
  };

  return (
    <div>
      <div className="add-user-button-container">
        <button
          onClick={() => {
            setShowAddForm(!showAddForm);
            setIsEditing(false);
          }}
          className="add-user-button"
        >
          Add User <span className="plus-sign">+</span>
        </button>
      </div>

      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search by name..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-bar"
        />
      </div>

      {/* View Mode Buttons */}
      <div className="view-mode-buttons">
        <button
          onClick={() => setViewMode("grid")}
          className={`view-button ${viewMode === "grid" ? "active" : ""}`}
        >
          Grid View
        </button>
        <button
          onClick={() => setViewMode("list")}
          className={`view-button ${viewMode === "list" ? "active" : ""}`}
        >
          List View
        </button>
      </div>

      {/* Add User Form */}
      {showAddForm && (
        <form onSubmit={handleAdd} className="user-form">
          <h3>Add New User</h3>
          <input
            type="text"
            placeholder="Name"
            value={newUser.name}
            onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
            required
          />
          <input
            type="url"
            placeholder="Profile Image URL"
            value={newUser.imageUrl}
            onChange={(e) => setNewUser({ ...newUser, imageUrl: e.target.value })}
          />
          <select
            value={newUser.role}
            onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
          >
            <option value="Admin">Admin</option>
            <option value="User">User</option>
          </select>
          <select
            value={newUser.status}
            onChange={(e) => setNewUser({ ...newUser, status: e.target.value })}
          >
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
          </select>
          <div className="form-buttons">
            <button type="submit" className="form-submit-button">
              Add
            </button>
            <button
              type="button"
              onClick={handleCancel}
              className="form-cancel-button"
            >
              Cancel
            </button>
          </div>
        </form>
      )}

      <div className="user-cards-container">
        {users.map((user) => (
          <div className="user-card-wrapper" key={user.id}>
            <div className="user-card">
              <div className="imgact">
              <img
                src={user.imageUrl || userimg} // Use userimg if no imageUrl is provided
                alt={`${user.name}'s profile`}
                className="user-image"
              />
              <div className="user-status">
                  <span className={`status-dot ${user.status === "Active" ? "active" : "inactive"}`}></span>
                </div>
              </div>
              <div className="user-details">
                <h4>{user.name}</h4>
                <p className="user-email">{user.email}</p>
                <p>{user.role}</p>
              </div>
            </div>
            <div className="user-actions-container">
              <button onClick={() => handleEdit(user)} className="edit-button">
                Edit
              </button>
              <button
                onClick={() => handleDelete(user.id)}
                className="delete-button"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserTable;
