import React, { useState, useEffect } from "react";
import "../assets/Styles/UserTable.css";

const UserTable = () => {
  const [users, setUsers] = useState([]);

  // Fetch data from json-server when the component mounts
  useEffect(() => {
    fetch("http://localhost:5000/users") // Fetching data from json-server
      .then((response) => response.json())
      .then((data) => setUsers(data)) // Update the state with fetched users
      .catch((error) => console.error("Error fetching users:", error)); // Handle errors
  }, []); // Empty dependency array ensures the fetch runs once when the component mounts

  const handleDelete = (id) => {
    // Here, we would delete the user both from the table and backend
    fetch(`http://localhost:5000/users/${id}`, {
      method: "DELETE", // DELETE request to remove the user from the server
    })
      .then(() => setUsers(users.filter((user) => user.id !== id))) // Remove the user locally
      .catch((error) => console.error("Error deleting user:", error)); // Handle errors
  };

  return (
    <div>
      <input type="text" placeholder="Search users..." />
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>{user.status}</td>
              <td>
                <button onClick={() => handleDelete(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserTable;
