import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom"; // Use useLocation to highlight active links
import "../assets/Styles/Sidebar.css";
import { FaHome, FaUsers, FaKey, FaShieldAlt, FaChevronLeft } from "react-icons/fa";

const Sidebar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const location = useLocation(); // Get the current path

  return (
    <aside className={`sidebar ${isCollapsed ? "collapsed" : ""}`}>
      <button
        className={`toggle-btn ${isCollapsed ? "rotate" : ""}`}
        onClick={() => setIsCollapsed(!isCollapsed)}
      >
        <FaChevronLeft />
      </button>
      <nav>
        <ul>
          <li className={location.pathname === "/" ? "active" : ""}>
            <Link to="/">
              <FaHome /> {!isCollapsed && "Dashboard"}
            </Link>
          </li>
          <li
            className={
              location.pathname.startsWith("/users") ? "active" : ""
            } // Highlight Users section if on any Users-related page
          >
            <Link to="/users">
              <FaUsers /> {!isCollapsed && "Users"}
            </Link>
            {/* Add User as a subsection */}
            {!isCollapsed && (
              <ul className="subsection">
                <li
                  className={
                    location.pathname === "/users/add" ? "active" : ""
                  }
                >
                  <Link to="/users/add">Add User</Link>
                </li>
              </ul>
            )}
          </li>
          <li className={location.pathname === "/roles" ? "active" : ""}>
            <Link to="/roles">
              <FaKey /> {!isCollapsed && "Roles"}
            </Link>
          </li>
          <li className={location.pathname === "/permissions" ? "active" : ""}>
            <Link to="/permissions">
              <FaShieldAlt /> {!isCollapsed && "Permissions"}
            </Link>
          </li>
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
